import React, { Component } from "react";
import Autocomplete from "./cocmponents/Autocomplete";

export default class App extends Component {
  constructor(props) {
    super();
    this.state = {
      list: true,
      card: false,
      artists: [],
      artist: {},
    };
  }

  componentDidMount() {
    fetch("http://localhost:3001/artists/list")
      .then((response) => response.json())
      .then((responseJson) => {
        this.setState({ artists: responseJson.data });
      });
  }

  showCard = (id) => {
    fetch(`http://localhost:3001/artists/${id}`)
      .then((response) => response.json())
      .then((responseJson) => {
        this.setState({ artist: responseJson.data });
      });
    this.setState({
      list: false,
      card: true,
    });
  };

  showList = () => {
    this.setState({
      card: false,
      list: true,
    });
  };

  render() {
    return (
      <div className="container">
        {this.state.list ? (
          <div className="list-group">
            <h1>Six Degrees of Separation</h1>
            <h2>Choose two artists to see how they are connected!</h2>
            {this.state.artists.map((artist) => (
              <li
                onClick={() => this.showCard(artist.id)}
                className="list-group-item list-group-item-action"
              >
                {artist.name}
              </li>
            ))}
          </div>
        ) : null}
        {this.state.card ? (
          <div class="card" style={{ width: "18rem" }}>
            <div class="card-body">
              <h5 class="card-title">{this.state.artist.name}</h5>
              <p class="card-text">{this.state.artist.runs}</p>
              <div onClick={() => this.showList()} class="btn btn-primary">
                Back
              </div>
            </div>
          </div>
        ) : null}
      </div>
    );
  }
}
